function floweringStep (plant: Sprite) {
    if (Math.percentChance(25)) {
        sprites.changeDataNumberBy(plant, "flowerSize", 1)
        plant.image.fillRect(sprites.readDataNumber(plant, "stemX") + Math.randomRange(-1, 1), plant.height - sprites.readDataNumber(plant, "height") - sprites.readDataNumber(plant, "flowerSize"), Math.min(sprites.readDataNumber(plant, "flowerSize"), 2), Math.min(sprites.readDataNumber(plant, "flowerSize"), 2), sprites.readDataNumber(plant, "color"))
        if (sprites.readDataNumber(plant, "flowerSize") == 3) {
            plant.setKind(SpriteKind.FullGrownFlower)
        }
    }
}
function growingStep (plant: Sprite) {
    sprites.changeDataNumberBy(plant, "growingHeight", 1)
    plant.image.setPixel(sprites.readDataNumber(plant, "stemX"), plant.height - sprites.readDataNumber(plant, "growingHeight"), 6)
    if (sprites.readDataNumber(plant, "growingHeight") == sprites.readDataNumber(plant, "height")) {
        plant.setKind(SpriteKind.Flower)
    }
}
